,t_prod as (
select boxsku ,sku ,DevelopLastAuditTime  ,ProductStatus 
from erp_product_products wl 
join tb on tb.arr = wl.BoxSku 
group by  boxsku ,sku ,DevelopLastAuditTime  ,ProductStatus 
)

, t_nomap as ( -- 产品表有 连接表无
select t_prod.boxsku 
-- ,t_prod.ProductStatus ,t_list.sku
from t_prod
left join t_list on t_prod.sku = t_list.sku
where t_list.sku is null 
) 


