select 
	boxsku
	,count(distinct concat(ShopCode ,SellerSKU ,ASIN )) `在线链接数`
from wt_listing wl 
join import_data.mysql_store ms 
	on wl.ShopCode = ms.Code 
	and wl.IsDeleted = 0 
	and ListingStatus = 1 
	and ms.ShopStatus = '正常'
	and department = '快百货'
join tb on tb.arr = wl.boxsku
-- where BoxSku = 4452433
group by boxsku




